module.exports = {
	getFixtures: function () {

		var teams = ['Arsenal', 'Barcelona', 'Chelsea', 'Derby', 'Everton', 'Fulham'];
		var listings = '';
		for (var i = 0; i < teams.length; i++) {
			var hometeam = teams[i];
			for (var j = i; j < teams.length; j++) {
				var awayteam = teams[j];
				if (awayteam != hometeam) {
					listings += '<br>' + hometeam + ' vs ' + awayteam;
				}
			}
		}
		return listings;
	}
};

